package web

import "html/template"

var (
	MapTemplate = template.Must(template.New("map").Parse(mapTemplateContents))
)

const mapTemplateContents = `<!doctype html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport"
          content="width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0">
    <meta http-equiv="X-UA-Compatible" content="ie=edge">
    <title>Monkey island UI</title>
    <!--<meta http-equiv="refresh" content="0.5">-->
    <style>
        .game-board { margin: auto; border-collapse: collapse; }
        .game-board tr, .game-board td { padding: 0; }
        .game-board td { width: 40px; height: 40px; background-size: cover; }
        .game-board td.empty { background-image: url(data:image/jpeg;base64,); }
        .game-board td.banana { background-image: url(data:image/jpeg;base64,); }
        .game-board td.wall { background-image: url(data:image/jpeg;base64,); }
        .game-board td.player-3 { background-image: url(data:image/jpeg;base64,); }
        .game-board td.player-4 { background-image: url(data:image/jpeg;base64,); }
        .logo-player { display: inline-block; width: 20px; height: 20px; background-size: cover; }
        .logo-player-3 { background-image: url('data:image/png;base64,'); }
        .logo-player-4 { background-image: url('data:image/png;base64,') }
	</style>
</head>
<body>
<div class="container">
	{{if .Map}}
		<div>
		{{range .Players}}
			<div>
			<div class="logo-player logo-player-{{.ID}}"></div>
			{{.Name}} - score : <span id="score-player-{{.ID}}">{{.Score}}</span>
			</div>
		{{end}}
		</div>
		<table class="game-board">
		{{range $y, $row := .Map}}
		<tr>
			{{range $x, $cell := $row}}
			{{if $cell.Empty }}
			<td class="empty" id="cell-{{$y}}-{{$x}}"></td>
			{{else if $cell.Banana}}
			<td class="banana" id="cell-{{$y}}-{{$x}}"></td>
			{{else if $cell.Wall}}
			<td class="wall" id="cell-{{$y}}-{{$x}}"></td>
			{{else if $cell.Player}}
			<td class="player-{{.}}" id="cell-{{$y}}-{{$x}}"></td>
			{{else}}
			<td></td>
			{{end}}
			{{end}}
		</tr>
		{{end}}
		</table>
	{{else}}
		<div>Game has not started yet.</div>
		<script>(function(){ setTimeout(function() { location.reload(); }, 500); })();</script>
	{{end}}
</div>
<script>
(function(){
  setInterval(function() {
    fetch("/map")
      .then(function(resp) { return resp.json(); })
      .then(updateScreen)
  }, 1000)

  function updateScreen(data) {
    data.gamers.forEach(function(gamer) {
      var playerScoreElement = document.querySelector("#score-player-"+gamer.id);
      if (playerScoreElement) {
      	playerScoreElement.innerHTML = gamer.score;
      }
    });
    data.map.forEach(function(row, y) {
      row.forEach(function(cell, x) {
        var className = ""
        switch (cell) {
          case 0:
            className = "empty"
            break;
          case 1:
            className = "banana"
            break;
          case 2:
            className = "wall"
            break;
          default:
            className = "player-" + cell
            break;
        }
        var cell = document.querySelector("#cell-"+y+"-"+x);
        if (cell) {
      	  cell.className = className;
        }
      });
    });
  }
})()

</script>
</body>
</html>`
